var events = require('events'), //
	util = require('util'), //
_ = require('underscore');

function ServerTool(config) {
	events.EventEmitter.call(this);
	_.extend(this,config);
	this.initialize();
}

ServerTool.prototype = {
	name: 'Server Tool',
	description: '',
	iconCls: '',
	
	initialize: function() {
		
	},
	start: function() {
		
	},
	message: function() {
		
	},
	exit: function() {
		
	},
}

function extend(proto) {
	var _class = function() { this.constructor.super_.apply(this,arguments); };
	_class.prototype = proto;
	util.inherits(_class,this);
	_class.extend = extend;
	return _class;
}
ServerTool.extend = extend;

util.inherits(ServerTool,events.EventEmitter);

module.exports = ServerTool;
